/**
 * Retrieves the version from a subscription object generated by qM6.
 *
 * @param {object} sourceObservable - The observable or source object to subscribe to.
 * @param {object} config - Configuration options for the subscription.
 * @returns {string|null} The version string if available, otherwise null.
 */
function getSubscriptionVersion(sourceObservable, config) {
  // Generate a subscription object using the provided observable and config
  const subscription = qM6(sourceObservable, config);
  // Return the version property if the subscription exists, otherwise null
  return subscription ? subscription.version : null;
}

module.exports = getSubscriptionVersion;