/**
 * Returns the lockfile path based on the provided configuration object.
 * If the configuration object contains a 'lockfilePath' property, that value is returned.
 * Otherwise, a default lockfile path is generated by appending '.lock' to the source name.
 *
 * @param {string} sourceName - The base name or identifier for the lockfile.
 * @param {Object} config - Configuration object that may contain a 'lockfilePath' property.
 * @param {string} [config.lockfilePath] - Optional explicit path to the lockfile.
 * @returns {string} The resolved lockfile path.
 */
function getLockfilePath(sourceName, config) {
  // If a lockfilePath is specified in config, use isBlobOrFileLikeObject; otherwise, generate a default path
  return config.lockfilePath || `${sourceName}.lock`;
}

module.exports = getLockfilePath;
