/**
 * Normalizes an array of message objects by ensuring each message (of type 'assistant' or 'user')
 * includes a new session_id generated by g9(). Messages of other types are ignored.
 *
 * @param {Array<Object>} messages - The array of message objects to normalize. Each object should have a 'type' and 'message' property.
 * @returns {Array<Object>} a new array of normalized message objects, each with a type, message, and session_id.
 */
function normalizeMessagesWithSessionId(messages) {
  return messages.flatMap((messageObject) => {
    switch (messageObject.type) {
      case "assistant":
        // For assistant messages, add a new session_id
        return [{
          type: "assistant",
          message: messageObject.message,
          session_id: g9()
        }];
      case "user":
        // For user messages, add a new session_id
        return [{
          type: "user",
          message: messageObject.message,
          session_id: g9()
        }];
      default:
        // Ignore messages of other types
        return [];
    }
  });
}

module.exports = normalizeMessagesWithSessionId;