/**
 * Creates an array of a specified length, where each element is generated by a provided function.
 *
 * @param {number} length - The desired length of the resulting array.
 * @param {function(number): any} generatorFunction - a function that takes the current index and returns the value for that position in the array.
 * @returns {Array<any>} An array populated with values returned by the generator function.
 */
function createArrayWithGenerator(length, generatorFunction) {
  // Initialize a new array with the specified length
  const resultArray = Array(length);

  // Populate each element in the array using the generator function
  for (let index = 0; index < length; index++) {
    resultArray[index] = generatorFunction(index);
  }

  return resultArray;
}

module.exports = createArrayWithGenerator;